WITH source_data AS (
    SELECT a.dv_hash_key_h_businessplan, a.dv_hashdiff_s_h_businessplan, a.[Profit], a.[Activate], a.[PublishType], a.[WFIsFinish], a.[IsDeleted], a.dv_load_timestamp, a.dv_recordsource
    FROM "DWH"."dbo"."hash__cmis_dbo_businessplans" AS a
    WHERE a.dv_hash_key_h_businessplan IS NOT NULL
),

latest_records AS (
    SELECT a.dv_hash_key_h_businessplan, a.dv_hashdiff_s_h_businessplan, a.dv_load_timestamp
    FROM (
        SELECT current_records.dv_hash_key_h_businessplan, current_records.dv_hashdiff_s_h_businessplan, current_records.dv_load_timestamp,
            RANK() OVER (
               PARTITION BY current_records.dv_hash_key_h_businessplan
               ORDER BY current_records.dv_load_timestamp DESC
            ) AS rank
        FROM "DWH"."dbo"."dv__s_h_businessplan" AS current_records
            JOIN (
                SELECT DISTINCT source_data.dv_hash_key_h_businessplan
                FROM source_data
            ) AS source_records
                ON current_records.dv_hash_key_h_businessplan = source_records.dv_hash_key_h_businessplan
    ) AS a
    WHERE a.rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.dv_hash_key_h_businessplan, stage.dv_hashdiff_s_h_businessplan, stage.[Profit], stage.[Activate], stage.[PublishType], stage.[WFIsFinish], stage.[IsDeleted], stage.dv_load_timestamp, stage.dv_recordsource
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.dv_hash_key_h_businessplan = stage.dv_hash_key_h_businessplan
        AND latest_records.dv_hashdiff_s_h_businessplan = stage.dv_hashdiff_s_h_businessplan
    WHERE latest_records.dv_hashdiff_s_h_businessplan IS NULL
)

SELECT * FROM records_to_insert