WITH source_data AS (
    SELECT a.dv_hash_key_h_contract, a.dv_hashdiff_s_h_contract, a.[Activate], a.dv_load_timestamp, a.dv_recordsource
    FROM "DWH"."dbo"."hash__cmis_dbo_contracts" AS a
    WHERE a.dv_hash_key_h_contract IS NOT NULL
),

latest_records AS (
    SELECT a.dv_hash_key_h_contract, a.dv_hashdiff_s_h_contract, a.dv_load_timestamp
    FROM (
        SELECT current_records.dv_hash_key_h_contract, current_records.dv_hashdiff_s_h_contract, current_records.dv_load_timestamp,
            RANK() OVER (
               PARTITION BY current_records.dv_hash_key_h_contract
               ORDER BY current_records.dv_load_timestamp DESC
            ) AS rank
        FROM "DWH"."dbo"."dv__s_h_contract" AS current_records
            JOIN (
                SELECT DISTINCT source_data.dv_hash_key_h_contract
                FROM source_data
            ) AS source_records
                ON current_records.dv_hash_key_h_contract = source_records.dv_hash_key_h_contract
    ) AS a
    WHERE a.rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.dv_hash_key_h_contract, stage.dv_hashdiff_s_h_contract, stage.[Activate], stage.dv_load_timestamp, stage.dv_recordsource
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.dv_hash_key_h_contract = stage.dv_hash_key_h_contract
        AND latest_records.dv_hashdiff_s_h_contract = stage.dv_hashdiff_s_h_contract
    WHERE latest_records.dv_hashdiff_s_h_contract IS NULL
)

SELECT * FROM records_to_insert