WITH source_data AS (
    SELECT a.dv_hash_key_h_department, a.dv_hashdiff_s_h_department, a.[ID], a.[GroupName], a.[GroupCode], a.[Year], a.[ParentID], a.[IsActive], a.[IsVirtual], a.dv_load_timestamp, a.dv_recordsource
    FROM "DWH"."dbo"."hash__cmis_dbo_groups" AS a
    WHERE a.dv_hash_key_h_department IS NOT NULL
),

latest_records AS (
    SELECT a.dv_hash_key_h_department, a.dv_hashdiff_s_h_department, a.dv_load_timestamp
    FROM (
        SELECT current_records.dv_hash_key_h_department, current_records.dv_hashdiff_s_h_department, current_records.dv_load_timestamp,
            RANK() OVER (
               PARTITION BY current_records.dv_hash_key_h_department
               ORDER BY current_records.dv_load_timestamp DESC
            ) AS rank
        FROM "DWH"."dbo"."dv__s_h_department" AS current_records
            JOIN (
                SELECT DISTINCT source_data.dv_hash_key_h_department
                FROM source_data
            ) AS source_records
                ON current_records.dv_hash_key_h_department = source_records.dv_hash_key_h_department
    ) AS a
    WHERE a.rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.dv_hash_key_h_department, stage.dv_hashdiff_s_h_department, stage.[ID], stage.[GroupName], stage.[GroupCode], stage.[Year], stage.[ParentID], stage.[IsActive], stage.[IsVirtual], stage.dv_load_timestamp, stage.dv_recordsource
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.dv_hash_key_h_department = stage.dv_hash_key_h_department
        AND latest_records.dv_hashdiff_s_h_department = stage.dv_hashdiff_s_h_department
    WHERE latest_records.dv_hashdiff_s_h_department IS NULL
)

