),

latest_records AS (
    SELECT a.dv_hash_key_h_estimatecontract, a.dv_hashdiff_s_h_estimatecontract, a.dv_load_timestamp
    FROM (
        SELECT current_records.dv_hash_key_h_estimatecontract, current_records.dv_hashdiff_s_h_estimatecontract, current_records.dv_load_timestamp,
            RANK() OVER (
               PARTITION BY current_records.dv_hash_key_h_estimatecontract
               ORDER BY current_records.dv_load_timestamp DESC
            ) AS rank
        FROM "DWH"."dbo"."dv__s_h_estimatecontract" AS current_records
