                SELECT DISTINCT source_data.dv_hash_key_h_estimatecontract
                FROM source_data
            ) AS source_records
                ON current_records.dv_hash_key_h_estimatecontract = source_records.dv_hash_key_h_estimatecontract
    ) AS a
    WHERE a.rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.dv_hash_key_h_estimatecontract, stage.dv_hashdiff_s_h_estimatecontract, stage.[Profit], stage.[Status], stage.[Activate], stage.[IsDeleted], stage.[PublishType], stage.[WFIsFinish], stage.dv_load_timestamp, stage.dv_recordsource
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.dv_hash_key_h_estimatecontract = stage.dv_hash_key_h_estimatecontract
        AND latest_records.dv_hashdiff_s_h_estimatecontract = stage.dv_hashdiff_s_h_estimatecontract
