WITH source_data AS (
    SELECT a.dv_hash_key_h_finalizationcontract, a.dv_hashdiff_s_h_finalizationcontract, a.[Profit], a.[Activate], a.[PublishType], a.[WFIsFinish], a.[IsDeleted], a.dv_load_timestamp, a.dv_recordsource
    FROM "DWH"."dbo"."hash__cmis_dbo_finalizationcontracts" AS a
    WHERE a.dv_hash_key_h_finalizationcontract IS NOT NULL
),

latest_records AS (
    SELECT a.dv_hash_key_h_finalizationcontract, a.dv_hashdiff_s_h_finalizationcontract, a.dv_load_timestamp
    FROM (
        SELECT current_records.dv_hash_key_h_finalizationcontract, current_records.dv_hashdiff_s_h_finalizationcontract, current_records.dv_load_timestamp,
            RANK() OVER (
               PARTITION BY current_records.dv_hash_key_h_finalizationcontract
               ORDER BY current_records.dv_load_timestamp DESC
            ) AS rank
        FROM "DWH"."dbo"."dv__s_h_finalizationcontract" AS current_records
            JOIN (
                SELECT DISTINCT source_data.dv_hash_key_h_finalizationcontract
                FROM source_data
            ) AS source_records
                ON current_records.dv_hash_key_h_finalizationcontract = source_records.dv_hash_key_h_finalizationcontract
    ) AS a
    WHERE a.rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.dv_hash_key_h_finalizationcontract, stage.dv_hashdiff_s_h_finalizationcontract, stage.[Profit], stage.[Activate], stage.[PublishType], stage.[WFIsFinish], stage.[IsDeleted], stage.dv_load_timestamp, stage.dv_recordsource
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.dv_hash_key_h_finalizationcontract = stage.dv_hash_key_h_finalizationcontract
        AND latest_records.dv_hashdiff_s_h_finalizationcontract = stage.dv_hashdiff_s_h_finalizationcontract
    WHERE latest_records.dv_hashdiff_s_h_finalizationcontract IS NULL
)

