WITH source_data AS (
    SELECT a.dv_hash_key_h_product, a.dv_hashdiff_s_h_product, a.[Code], a.[Name], a.[Description], a.[Activate], a.[IsDeleted], a.[ParentID], a.[ADN], a.dv_load_timestamp, a.dv_recordsource
    FROM "DWH"."dbo"."hash__cmis_dbo_branches" AS a
    WHERE a.dv_hash_key_h_product IS NOT NULL
),

latest_records AS (
    SELECT a.dv_hash_key_h_product, a.dv_hashdiff_s_h_product, a.dv_load_timestamp
    FROM (
        SELECT current_records.dv_hash_key_h_product, current_records.dv_hashdiff_s_h_product, current_records.dv_load_timestamp,
            RANK() OVER (
               PARTITION BY current_records.dv_hash_key_h_product
               ORDER BY current_records.dv_load_timestamp DESC
            ) AS rank
        FROM "DWH"."dbo"."dv__s_h_product" AS current_records
            JOIN (
                SELECT DISTINCT source_data.dv_hash_key_h_product
                FROM source_data
            ) AS source_records
                ON current_records.dv_hash_key_h_product = source_records.dv_hash_key_h_product
    ) AS a
    WHERE a.rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.dv_hash_key_h_product, stage.dv_hashdiff_s_h_product, stage.[Code], stage.[Name], stage.[Description], stage.[Activate], stage.[IsDeleted], stage.[ParentID], stage.[ADN], stage.dv_load_timestamp, stage.dv_recordsource
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.dv_hash_key_h_product = stage.dv_hash_key_h_product
        AND latest_records.dv_hashdiff_s_h_product = stage.dv_hashdiff_s_h_product
    WHERE latest_records.dv_hashdiff_s_h_product IS NULL
)

SELECT * FROM records_to_insert