    FROM "DWH"."dbo"."hash__cmis_dbo_users" AS a
    WHERE a.dv_hash_key_h_user IS NOT NULL
),

latest_records AS (
    SELECT a.dv_hash_key_h_user, a.dv_hashdiff_s_h_user, a.dv_load_timestamp
    FROM (
        SELECT current_records.dv_hash_key_h_user, current_records.dv_hashdiff_s_h_user, current_records.dv_load_timestamp,
            RANK() OVER (
               PARTITION BY current_records.dv_hash_key_h_user
               ORDER BY current_records.dv_load_timestamp DESC
            ) AS rank
        FROM "DWH"."dbo"."dv__s_h_user" AS current_records
