                SELECT DISTINCT source_data.dv_hash_key_h_user
                FROM source_data
            ) AS source_records
                ON current_records.dv_hash_key_h_user = source_records.dv_hash_key_h_user
    ) AS a
    WHERE a.rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.dv_hash_key_h_user, stage.dv_hashdiff_s_h_user, stage.[ID], stage.[FirstName], stage.[LastName], stage.[EmployeeCode], stage.[Gender], stage.[Active], stage.[BeginWorkDate], stage.[TitleID], stage.dv_load_timestamp, stage.dv_recordsource
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.dv_hash_key_h_user = stage.dv_hash_key_h_user
        AND latest_records.dv_hashdiff_s_h_user = stage.dv_hashdiff_s_h_user
